'use strict';

const expect = require('chai').expect;
const atify = require('../').atify;
const parse = require('../').parse;

describe('.atify', function() {
  it('works', function() {
    expect(atify(parse('@glimmer/compiler@1.1.1'))).to.eql('compiler-at-glimmer');
    expect(atify(parse('compiler@1.1.1'))).to.eql('compiler');
  });
});
