'use strict';

const expect = require('chai').expect;
const dasherize = require('../').dasherize;

describe('.dasherize', function() {
  it('doesn\'t modify names without capitals', function() {
    expect(dasherize('foo')).to.equal('foo');
  });

  it('handles initial capitals', function() {
    expect(dasherize('Foo')).to.equal('foo');
  });

  it('handles multiple words', function() {
    expect(dasherize('FooBar')).to.equal('foo-bar');
  });

  it('handles all-caps words', function() {
    expect(dasherize('FooBARBaz')).to.equal('foo-bar-baz');
  });
});
